from string import ascii_uppercase

alphabet = 'mjuanedsc-oxgkhqvftibzwpr-'
#           ABCDEFGHIJKLMNOPQRSTUVWXYZ

text = '{K6F4G_4K41R515_15_73A10B5_702E03EU}'.lower()

dec = ''

for c in text:
    if c in alphabet:
        dec += ascii_uppercase[alphabet.index(c)]
    else:
        dec += c

print(dec)

